package cn.dogplanet.entity;import java.util.ArrayList;import java.util.List;public class ShopBuyDetail {	public static final String TYPE_DETAIL="1";	public static final String TYPE_CART="2";	private String type;	private String id;	private String category;	private String imgUrl;	private String main_name;	private String child_name;	private String num;	private String price;	private String time;	private ArrayList<String> ids;	public String getImgUrl() {		return imgUrl;	}	public void setImgUrl(String imgUrl) {		this.imgUrl = imgUrl;	}	public String getMain_name() {		return main_name;	}	public void setMain_name(String main_name) {		this.main_name = main_name;	}	public String getChild_name() {		return child_name;	}	public void setChild_name(String child_name) {		this.child_name = child_name;	}	public String getNum() {		return num;	}	public void setNum(String num) {		this.num = num;	}	public String getPrice() {		return price;	}	public void setPrice(String price) {		this.price = price;	}	public String getTime() {		return time;	}	public void setTime(String time) {		this.time = time;	}	@Override	public String toString() {		return "ShopBuyDetail [imgUrl=" + imgUrl + ", main_name=" + main_name				+ ", child_name=" + child_name + ", num=" + num + ", price="				+ price + ", time=" + time + "]";	}	public String getId() {		return id;	}	public void setId(String id) {		this.id = id;	}	public String getCategory() {		return category;	}	public void setCategory(String category) {		this.category = category;	}	public String getType() {		return type;	}	public void setType(String type) {		this.type = type;	}	public ArrayList<String> getIds() {		return ids;	}	public void setIds(ArrayList<String> ids) {		this.ids = ids;	}}